import './App.css';
import { BrowserRouter, Route, NavLink, Switch, Redirect, withRouter } from 'react-router-dom';
import React, { useState } from 'react';
import Accueil from './Composants/Accueil.jsx';
import Pays from './Composants/Pays.jsx';
import Page404 from './Composants/Page404.jsx';
import Recherche from './Composants/Recherche.jsx';
import { Menu } from 'semantic-ui-react';

const CheckProps = (props) => {
  return (
    <div>
      <button onClick={() => props.history.push({
        pathname: '/Recherche',
        state: { params: 'abcdef', usager:'haythem' }
      })}>Aller sur la Faq</button>

    </div>
  )
}


function App() {
  return (
    <React.Fragment>
      <BrowserRouter>
        <CheckPropsWithRouter />
        <header>
          <h2>Menu</h2>
          <ul>
            <li><NavLink activeClassName="lien-actif" activeStyle={{ color: "red", fontWeight: "bold" }} to="/" exact>Accueil</NavLink></li>
            <li><NavLink activeClassName="lien-actif" activeStyle={{ color: "red", fontWeight: "bold" }} to="/Recherche">Recherche</NavLink></li>
            <li><NavLink activeClassName="lien-actif" activeStyle={{ color: "red", fontWeight: "bold" }} to="/Pays">Pays</NavLink></li>
          </ul>

          <Menu>
            <Menu.Item>Accueil</Menu.Item>
            <Menu.Item>Recherche</Menu.Item>
            <Menu.Item>Pays</Menu.Item>
          </Menu>
        </header>
        <Switch>
          <Route path="/" component={Accueil} exact />
          <Route path="/Recherche" component={Recherche} />
          <Route path="/Pays" component={Pays} />
          <Route path="*" component={Page404} ch />
        </Switch>
      </BrowserRouter>
    </React.Fragment>
  );
}

export default App;
