import { useState } from "react";
import { Container, Label, Input, Button } from "semantic-ui-react";

const Recherche = (props) => {
    const [nom, setNom] = useState("");
    const [data, setData] = useState([]);

    const onClick = () => {
        fetch(`https://restcountries.eu/rest/v2/name/${nom}?fields=name;alpha2Code;flag`)
            .then((response) => response.json())
            .then((data) => console.log(data))
        .catch((erreur))
        
    }

    return (
        <Container>
            <h1>Recherche</h1>
            <Label pointing="right">Pays</Label>
            <Input type="text" value={nom} onChange={(e) => { setNom(e.target.value); onClick(); }} />
            <Button onClick={onClick}>Rechercher les pays</Button>
        </Container>
    )
}

export default Recherche;
