import Accueil from './Composants/Accueil';





const Cgv = () => {
  return (
    <div>
      Je suis le Cgv
    </div>
  )
}

const Page404 = () => {
  const [connected, setConnected] = useState(false);

  if (!connected) {
    return (
      <div>
        <p>Vous n'êtes pas connecté</p>
        <button onClick={() => setConnected(true)}>Se connecter</button>
      </div>
    )
  }
  return (
    <Redirect to='/' />
  )
}

